    --link-color: @accent-color;
    --page-background-color: @base;
    --info-color: @blue;
    --success-color: @green;
    --error-color: @red;
    --error-text-color: @text;
    --warning-color: @yellow;
    --button-font-color: @text;
    --widget-background-color: @base;
    --presentational-content-color: @overlay2;
    --gray-lightest: @base;
    --gray-light: @crust;
    --gray-mid: @surface0;
    --gray-dark: @overlay2;
    --gray-darkest: @text;

    .c4ca43590 {
